/* using: add "data-maxlength" attribute to textarea
 * add one span exactly after textarea to show the remaining chars
 * Ex:
 * <textarea ... data-maxlength='VALUE' [data-maxlength-init='true/false'] [option-lang='en']>
 * <span [class="maxlength-remain"]></span>
 */
function ShowMaxLengthHint(n){var i=parseInt(n.attr("data-maxlength")),r=n.attr("option-lang"),u=n.val(),f=u.length,t;t=r=="en"?"remaining letters: ":"تعداد حروف باقیمانده: ";n.next().html(t+(i-f))}$(document).ready(function(){$("textarea[data-maxlength-init]").each(function(){$(this).attr("data-maxlength-init")=="true"&&ShowMaxLengthHint($(this))});$("textarea[data-maxlength]").keypress(function(n){var t=n.which;if(t>=33||t==13||t==32){var i=parseInt($(this).attr("data-maxlength")),r=$(this).val(),u=r.length;u>=i&&n.preventDefault()}});$("textarea[data-maxlength]").keyup(function(){ShowMaxLengthHint($(this))})});